sys_ui_action.script:
function cancelOrder(){

	var state = g_form.getValue('status');

	var cancelMsg = "This order has been cancelled";

	if(state == 'draft' || state == 'pending approval') {
		if(state == 'draft') {
			//set cancelled values only
			g_form.setValue('status','order cancelled');
			g_form.setValue('order_updates',cancelMsg);
			g_form.save();
		} else {
			//set cancelled values and run business rule
			g_form.setValue('status','order cancelled');
			g_form.setValue('order_updates',cancelMsg);
			g_form.save();
			//fire the server-side code.
			gsftSubmit(gel('cancel_order'));
		}
	} else {
		//unable to cancel
		g_form.addErrorMessage('Unable to cancel order');
	}
}

if(typeof window == 'undefined'){
	runCancelWF();
}

function runCancelWF(){
	//set a redirect action
	action.setRedirectURL(current);
	//load workflow helper
	var w = new Workflow();
	var gr = new GlideRecord('wf_context');
	// get the WF context of the current record and cancel it.
	if (gr.get(current.context)){
		w.cancelContext(gr);
	}
}
